@testable import PokeDex
import XCTest

class PokemonsPresenterTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: PokemonsPresenter!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupPokemonsPresenter()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupPokemonsPresenter() {
        sut = PokemonsPresenter()
    }
    
    // MARK: Test doubles
    
    class PokemonsDisplayLogicSpy: PokemonsDisplayLogic {
        var displaySomethingCalled = false
        
        func displaySomething(viewModel: Pokemons.Something.ViewModel) {
            displaySomethingCalled = true
        }
    }
    
    // MARK: Tests
    
    func testPresentSomething() {
        // Given
        let spy = PokemonsDisplayLogicSpy()
        sut.viewController = spy
        let response = Pokemons.Something.Response()
        
        // When
        sut.presentSomething(response: response)
        
        // Then
        XCTAssertTrue(spy.displaySomethingCalled, "presentSomething(response:) should ask the view controller to display the result")
    }
}
